mapper.update:
update
user_borrow_bid_apply_record
set
surplus_amount = #{bidSurplusAmount},
version = version+1
where
id=#{borrowID}
and
borrow_number = #{borrowNumber}
and
user_id=#{borrowConsumerID}
and
version = #{version}

update
user_borrow_bid_apply_record
set
full_tender_time=#{fullTime},
is_full=#{isFull}
where
id=#{borrowID}
and
user_id=#{borrowConsumerID}
and
borrow_number=#{borrowNumber}

mapper.select:
select record.*, repay.repayment_description as repayMode from user_borrow_bid_apply_record as record left join consumer_repayment_type AS repay ON record.repay_type = repay.id
publish_id > 0 and trals_id = 0 and retrals_id = 0 and abandon_id = 0 -- 项目期限
and borrow_time_limit >= #{minTerm}
and borrow_time_limit <= #{maxTerm}
-- 参考利率
and lending_rate >= #{minAnnualRate}
and lending_rate <= #{maxAnnualRate}
-- 还款方式
and repay_type = #{repayType}
-- 剩余金额
and surplus_amount >= #{minSurplusAmount}
and surplus_amount <= #{maxSurplusAmount}
-- 参考利率排序方式 1：降序，2：升序
order by lending_rate desc
order by lending_rate asc
-- 期限排序方式 1：降序，2：升序
order by borrow_time_limit desc
order by borrow_time_limit asc
-- 剩余金额排序 1：降序，2：升序
order by surplus_amount desc
order by surplus_amount asc
limit #{pageCurrent}, #{pageSize}

select
count(*)
from
user_borrow_bid_apply_record

    publish_id > 0
    and trals_id = 0
    and retrals_id = 0
    and abandon_id = 0
    -- 项目期限

        and borrow_time_limit >= #{minTerm}


        and borrow_time_limit <= #{maxTerm}

    -- 参考利率

        and lending_rate >= #{minAnnualRate}


        and lending_rate <= #{maxAnnualRate}

    -- 还款方式

        and repay_type = #{repayType}

    -- 剩余金额

        and surplus_amount >= #{minSurplusAmount}


        and surplus_amount <= #{maxSurplusAmount}
